import * as React from "react";
import AppBar from "@mui/material/AppBar";
import Box from "@mui/material/Box";
import Toolbar from "@mui/material/Toolbar";
import Typography from "@mui/material/Typography";
import { Avatar, Card, Grid, IconButton, Paper, Skeleton } from "@mui/material";
import Grid2 from "@mui/material/Unstable_Grid2/Grid2";
import Header from "../components/header/Header";
import Poster from "../components/Poster";
import { useGetMoviesQuery } from "../reducer/apiSlice";
import { useTheme } from "@emotion/react";

export default function RecentMovies() {
    const { data, isFetching } = useGetMoviesQuery();

    const theme = useTheme()

    return (
        <Grid2 container display="grid" gap={5}>
            <Typography variant="h5" color="text.secondary">
                Recent Movies
            </Typography>
            <Box sx={{ overflowX: "auto" }}>
                <Grid container columnGap={2} flexWrap="nowrap">
                    {isFetching ? (
                        <Skeleton />
                    ) : (
                        data.Search.map((item) => (
                            <Grid>
                                <Grid2 sx={{ width: "84px" }}>
                                    <img
                                        src={item.Poster}
                                        alt=""
                                        width={84}
                                        height={108}
                                        style={{borderRadius: 4, border: `1px solid ${theme.palette.text.secondry}`}}
                                    />
                                    <Typography>{item.Title}</Typography>
                                </Grid2>
                            </Grid>
                        ))
                    )}
                </Grid>
            </Box>
        </Grid2>
    );
}
